import { Component } from '@angular/core'
import { ThemeConstantService } from '../../shared/services/theme-constant.service';

@Component({
    template: `
        <canvas ngChartjs 
            [datasets]="barChartData" 
            [labels]="barChartLabels"
            [options]="barChartOptions" 
            [colors]="barChartColors"
            [legend]="barChartLegend" 
            [chartType]="barChartType">
        </canvas>
    `
})

export class barChartJsExampleComponent {
    //Bar Chart
    barChartOptions: any = {
        scaleShowVerticalLines: false,
        responsive: true,
        scales: {
            xAxes: [{
                display: true,
                scaleLabel: {
                    display: false,
                    labelString: 'Month'
                },
                gridLines: false,
                ticks: {
                    display: true,
                    beginAtZero: true,
                    fontSize: 13,
                    padding: 10
                }
            }],
            yAxes: [{
                display: true,
                scaleLabel: {
                    display: false,
                    labelString: 'Value'
                },
                gridLines: {
                    drawBorder: false,
                    offsetGridLines: false,
                    drawTicks: false,
                    borderDash: [3, 4],
                    zeroLineWidth: 1,
                    zeroLineBorderDash: [3, 4]
                },
                ticks: {
                    max: 100,                            
                    stepSize: 20,
                    display: true,
                    beginAtZero: true,
                    fontSize: 13,
                    padding: 10
                }
            }]
        }
    };
    barChartLabels: string[] = ['2006', '2007', '2008', '2009', '2010', '2011',];
    barChartType = 'bar';
    barChartLegend = true;
    barChartColors: Array<any> = [
        { 
            backgroundColor: this.themeColors.blue,
            borderWidth: 0
        },
        { 
            backgroundColor: this.themeColors.blueLight,
            borderWidth: 0
        }
    ];
    barChartData: any[] = [
        { 
            data: [65, 59, 80, 81, 56, 55],
            label: 'Series A',
            categoryPercentage: 0.35,
            barPercentage: 0.70,
        },
        { 
            data: [28, 48, 40, 19, 86, 27],
            label: 'Series B',
            categoryPercentage: 0.35,
            barPercentage: 0.70,
        }
    ];
}
